//import logo from './logo.svg';
import './App.css';
import React, {useEffect, useState} from 'react';
import Transactions from './components/Transactions';
import NewTransactionForm from './components/NewTransactionForm';




function App() {
  const [transactions, setTransactions] = useState([])
  useEffect(() => {
    fetch("http://localhost:3000/transactions")
    .then(response => response.json())
    .then(data => setTransactions(data))
  },[])

  console.log(transactions)
function handleUpdate(newTransaction){
  console.log(newTransaction)
  setTransactions(currentTransaction=>[...currentTransaction,newTransaction])

  const serverOptions={
    method: 'POST',
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify(newTransaction)
  }
  fetch("http://localhost:3000/transactions",serverOptions)
  .then(response => response.json())
  .then(newItem=>console.log(newItem))
}

  return (
    <div>
      <div>
        <h1> Bank of Flatiron</h1>
      </div>
        <searchForm on/>
        <NewTransactionForm onSubmission={handleUpdate}/>
        <Transactions transactions={transactions}/>
    </div>

  );
}

export default App;
